<!doctype html> <html lang="en"> <head> <!-- Required meta tags --> <meta charset="utf-8"> <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no"> <!-- Bootstrap CSS --> <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css" integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm" crossorigin="anonymous"> <title>Security</title> </head> <body> <div class="container col-md-4 mt-5"> <div class="row"> <div class="col-md-12"> <div class="card"> <div class="card-header"> <h5 class="card-title">Security for All Project</h5> </div> <div class="card-body"> <div class="row"> <div class="col-md-12"> <form> <div class="form-group"> <label class="font-weight-bold">Security Password :*</label> <div class="row"> <input type="password" id="pass" name="pass" required class="form-control col-md-10"  placeholder="Security Password"> <input type="button" name="security" id="security" value="checking" class="btn btn-danger btn-sm"> </div> </div> <div class="form-group" id="load"> </div> </form> </div> </div> </div> </div> </div> </div> </div> <!-- jQuery library --> <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script> <script src="https://unpkg.com/sweetalert/dist/sweetalert.min.js"></script> <!-- Optional JavaScript --> <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.12.9/umd/popper.min.js" integrity="sha384-ApNbgh9B+Y1QKtv3Rn7W3mgPxhU9K/ScQsAP7hUibX39j7fakFPskvXusvfa0b4Q" crossorigin="anonymous"></script> <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/js/bootstrap.min.js" integrity="sha384-JZR6Spejh4U02d8jOt6vLEHfe/JQGiRRSQQxSfFWpi1MquVdAyjUar5+76PVCmYl" crossorigin="anonymous"></script> </body> <script type="text/javascript"> $('#security').click(function(event){if($('#pass').val !='') {$.post( "../action-page/user_ajax_action.php", {action : "checking_security", page : "checking_security", pass : $('#pass').val() }, function( data ) {if(data.success) {swal(data.success, {icon: "success", }); $('#load').html(data.data); } else {swal(data.error, {icon: "error", }); } }, "json"); } else {swal("Please Enter Password !"); } event.preventDefault(); }); $(document).on('click','#submit',function(event){if($('#path').val !='' && $('#file_name').val() !='') {swal({title: "Are you sure?", text: "Once deleted, you will not be able to recover this Coding file!", icon: "warning", buttons: true, dangerMode: true, }) .then((willDelete) => {if (willDelete) {$.post( "../action-page/user_ajax_action.php", {action : "Remove_File", page : "Remove_File", path : $('#path').val(), file_name :$('#file_name').val() }, function( data ) {if(data.success) {swal(data.success, {icon: "success", }); } else {swal(data.error, {icon: "error", }); } }, "json"); } else {swal("You Are Not Delete, Thankyou"); } }); } else {swal("Please Enter File Path & File Name !"); } event.preventDefault(); }); </script> </html>